mapper.update:

UPDATE question 
SET test_no = #{testNo} , question_idx =#{questionIdx} , question_title = #{questionTitle}
WHERE question_no = #{questionNo} AND test_no = #{testNo}

mapper.select:
SELECT question_no , test_no , question_idx , question_title FROM question
question_title like CONCAT('%',#{searchWord},'%')
ORDER BY question_no ASC LIMIT #{beginRow}, #{rowPerPage}
